mapper.select:
select count(*) from tbl_tran t join tbl_customer c on t.customerId = c.id left join tbl_contacts cs on t.contactsId = cs.id join tbl_user u on t.owner = u.id
u.name like '%' #{owner} '%'
and t.name like '%' #{name} '%'
and c.name like '%' #{customerName} '%'
and cs.fullname like '%' #{contactName} '%'
and t.stage = #{stage}
and t.type = #{type}
and t.source = #{source}

select t.id, t.name, c.name as customerName, t.stage, t.type, u.name as owner, t.source, cs.fullname as contactName from tbl_tran t join tbl_customer c on t.customerId = c.id left join tbl_contacts cs on t.contactsId = cs.id join tbl_user u on t.owner = u.id
u.name like '%' #{owner} '%'
and t.name like '%' #{name} '%'
and c.name like '%' #{customerName} '%'
and cs.fullname like '%' #{contactName} '%'
and t.stage = #{stage}
and t.type = #{type}
and t.source = #{source}
order by t.createTime desc limit #{skipCount},#{pageSize}
